        CounterSet cs = (CounterSet)serializer.Deserialize(new StringReader("<CounterSet name=\"MyCounters\"></CounterSet>"));

        if (cs.Counters != null)
        {
            Console.WriteLine($"PASSED: Counters.Count={cs.Counters.Count}");
        }
        else
